import arboretum
import numpy as np
from sklearn.datasets import load_iris
import json
import pytest


def run_regression(depth, true_values, trees=1, double_precision=True, use_hist_trick=True, upload_features=True):
        # load test data
    iris = load_iris()
    n = 10000

    y = iris.target != 2

    print(iris.data.shape)

    data = arboretum.DMatrix(iris.data, y=y)

    config = json.dumps({'objective': 1,
                         'method': 1,
                         'internals':
                         {
                             'double_precision': double_precision,
                             'compute_overlap': 2,
                             'use_hist_subtraction_trick': use_hist_trick,
                             'upload_features': upload_features,
                         },
                         'tree':
                         {
                             'eta': 0.2,
                             'max_depth': depth,
                             'gamma': 0.0,
                             'min_child_weight': 2.0,
                             'min_leaf_size': 0,
                             'colsample_bytree': 1.0,
                             'colsample_bylevel': 1.0,
                             'lambda': 0.0,
                             'alpha': 0.0
                         }})

    model = arboretum.Garden(config, data)

    # grow trees
    for i in range(trees):
        model.grow_tree()

    # predict on train data set
    pred = model.predict(data)
    print(true_values)
    print(pred)
    assert np.allclose(pred, true_values)


